import React,{useState} from 'react';
import { Link,Navigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
    const [auth,setAuth] = useState(false);
    const [data,setData] = useState({
        email : '',
        password : '',
    })
    const changeHandler= e =>{
        setData({...data,[e.target.name]:e.target.value})
    }
    const submitHandler = e =>{
        e.preventDefault();
       axios.post('http://localhost:5000/Login',data).then(
        res => {localStorage.setItem('token',res.data.token);setAuth(true)}
       )
    }
    if(auth){
        return (
            <Navigate to="/Dashboard" replace={true} />
        )
      }
    return(
        <div style={{textAlign:"center"}}>
            <nav className="navbar bg-dark" style={{display:'flex',alignItems:'center',justifyContent:'space-between',paddingTop:10,paddingLeft:10,paddingRight:10,backgroundColor:'Pink'}}>
 <h1>
   <Link to="/"><i className="fas fa-code"></i>Developers Hub</Link>
 </h1>

  <button style={{borderRadius:30,padding:10,backgroundColor:'PaleVioletRed'}}><Link to="/Register">Register </Link></button>
  <button style={{borderRadius:30,padding:10,backgroundColor:'PaleVioletRed'}}><Link to="/Login">Login </Link></button>
</nav>
<section className="container">
  <h1 className="large text-primary" style={{fontSize:50}}>Sign Up </h1>
  <p className ="lead" style={{fontSize:25}}><i className="fas fa-user"></i> Sign into Your Account </p>
  <form className="form" onSubmit={submitHandler} autoComplete="off">
  
  <div className="form-group">
    <input type="email" placeholder="Email Address" name="email" onChange={changeHandler} required />
   </div><br></br>
<div className="form-group">
   <input
     type="password"
     placeholder="Password"
     name="password" 
     onChange={changeHandler} />
</div><br></br>
 
   <input type="submit" className="btn btn-primary" value="Login" style={{borderRadius:30,padding:10,backgroundColor:'PaleVioletRed'}}/>
</form>
<p className="my-1"> Don't have any account?<Link to="/Register">Sign Up </Link>
</p>
</section>

        </div>
    )
}

export default Login
