import React,{useState,useEffect} from 'react';
import { Link,Navigate } from 'react-router-dom';
import axios from 'axios';
const Dashboard = () => {
    const [data,setData]=useState([]);
    useEffect(()=>{
       axios.get('http://localhost:5000/allprofiles',{
        headers : {
            'x-token' : localStorage.getItem('token')
        }
       }).then(res => setData(res.data))
    },[])
    if(!localStorage.getItem('token')){
        return <Navigate to="/Login" replace={true} />
    }
    return (
        <div>
            <nav className="navbar bg-dark">
  <h1>
    <Link to="/"><i className="fas fa-code"></i> Developers Hub</Link>
 </h1>
 <ul>
   <li><Link to="/Myprofile">My Profile</Link></li>
   <li><Link to="/Login" onClick={() => localStorage.removeItem('token')}>Logout</Link></li>
 </ul>
</nav>
<section className="cointainer">
 <h1 className="large text-primary"> Developers</h1>
 <p className="lead">
    <i className="fab fa-connectdevelop"></i>Browse and connect with developers
 </p>
 <div className="profiles">
    {data.length>=1 ? 
    data.map(profile =>
        <div className="profile bg-light">
        <img
         className="round-img"
         src=""
         alt=""
        />
        <div>
        <h2> {profile.name}</h2>
        <p> {profile.email}</p>
        <p> India</p>
        <Link to="profile.html" className="btn btn-primary">View Profile</Link>
        </div> 
         <ul>
            {profile.skill}
           
          </ul>
        </div> )
    : null}
   
    </div>
</section>
</div>
    )
}
export default Dashboard
